// src/components/CarGrid.js
import React from 'react';
import { FaCar } from 'react-icons/fa';

const CarGrid = ({ cars, setSelectedCar, setCurrentImageIndex, getCarImages }) => {
  return (
    <div className="cars-grid">
      {cars.map(car => {
        const images = getCarImages(car.id);
        const firstImage = images.length > 0 ? images[0] : null;

        return (
          <div
            key={car.id}
            className="car-card"
            onClick={() => {
              setSelectedCar(car);
              setCurrentImageIndex(0);
            }}
          >
            <div className="car-image">
              {firstImage ? (
                <img src={firstImage} alt={`${car.marca} ${car.modelo}`} />
              ) : (
                <FaCar className="car-placeholder-icon" />
              )}
            </div>
            <div className="car-info">
              <h3>{car.marca} {car.modelo}</h3>
              <p>Ano: {car.ano}</p>
              <p>Pre√ßo: R$ {car.preco.toLocaleString('pt-BR')}</p>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default CarGrid;
