import React, { useState, useEffect } from 'react';
import { FaCar, FaUser, FaBell, FaSignOutAlt, FaSearch, FaCalendarAlt, FaUserEdit, FaBars, FaEdit, FaSave, FaTimes } from 'react-icons/fa';
import { 
  getVehicles, 
  getReservations, 
  getNotifications, 
  markNotificationAsRead,
  createReservation,
  cancelReservation,
  updateUserProfile 
} from '../../services/api';
import '../../assets/styles/ClienteDashboard.css';

const ClienteDashboard = ({ onNavigate }) => {
  const [activeTab, setActiveTab] = useState('explorar');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [cars, setCars] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [notificacoes, setNotificacoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showNotifications, setShowNotifications] = useState(false);
  const [selectedCar, setSelectedCar] = useState(null);
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    nome: '',
    email: '',
    telefone: ''
  });
  const [editError, setEditError] = useState('');

  useEffect(() => {
    fetchData();
    const userData = JSON.parse(localStorage.getItem('user'));
    setUser(userData);
    if (userData) {
      setEditForm({
        nome: userData.nome || '',
        email: userData.email || '',
        telefone: userData.telefone || ''
      });
    }
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [vehiclesRes, notificationsRes, reservationsRes] = await Promise.all([
        getVehicles(),
        getNotifications(),
        getReservations()
      ]);

      if (vehiclesRes.data.veiculos) {
        setCars(vehiclesRes.data.veiculos);
      }
      if (notificationsRes.data.notificacoes) {
        setNotificacoes(notificationsRes.data.notificacoes);
      }
      if (reservationsRes.data.reservas) {
        setReservations(reservationsRes.data.reservas);
      }
    } catch (err) {
      setError('Erro ao carregar dados');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleMarcarNotificacaoLida = async (notificacaoId) => {
    try {
      await markNotificationAsRead(notificacaoId);
      setNotificacoes(notificacoes.map(notificacao => 
        notificacao.id === notificacaoId 
          ? { ...notificacao, lida: true }
          : notificacao
      ));
    } catch (err) {
      console.error('Erro ao marcar notificação como lida:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onNavigate('landing');
  };

  const handlePreOrder = async (carId) => {
    try {
      const response = await createReservation(carId);
      if (response.data) {
        setSelectedCar(null);
        fetchData(); // Refresh all data
      }
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao fazer pré-reserva');
    }
  };

  const handleCancelReservation = async (reservaId) => {
    try {
      await cancelReservation(reservaId);
      fetchData(); // Refresh all data
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao cancelar reserva');
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setEditError('');

    try {
      const response = await updateUserProfile(editForm);
      if (response.mensagem === 'Dados pessoais atualizados com sucesso') {
        // Update local storage and state with new user data
        const updatedUser = { ...user, ...editForm };
        localStorage.setItem('user', JSON.stringify(updatedUser));
        setUser(updatedUser);
        setIsEditing(false);
      }
    } catch (err) {
      setEditError(err.mensagem || 'Erro ao atualizar perfil');
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'explorar':
        return (
          <div className="cars-grid">
            {cars.map(car => (
              <div key={car.id} className="car-card" onClick={() => setSelectedCar(car)}>
                <div className="car-icon">
                  <FaCar size={40} />
                </div>
                <div className="car-info">
                  <h3>{car.marca} {car.modelo}</h3>
                  <p>Ano: {car.ano}</p>
                  <p>Preço: R$ {car.preco.toLocaleString('pt-BR')}</p>
                </div>
              </div>
            ))}
          </div>
        );
      case 'reservas':
        return (
          <div className="reservas-section">
            <h2>Minhas Reservas</h2>
            <div className="reservas-grid">
              {reservations.map(reserva => (
                <div key={reserva.id} className="reserva-card">
                  <div className="car-icon">
                    <FaCar size={40} />
                  </div>
                  <div className="reserva-info">
                    <h3>{reserva.marca} {reserva.modelo}</h3>
                    <p>Ano: {reserva.ano}</p>
                    <p>Data da Reserva: {new Date(reserva.data_reserva).toLocaleDateString()}</p>
                    <p>Status: {reserva.status}</p>
                    {reserva.status === 'pendente' && (
                      <button 
                        className="btn btn-danger"
                        onClick={() => handleCancelReservation(reserva.id)}
                      >
                        Cancelar Reserva
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'perfil':
        return (
          <div className="perfil-section">
            <div className="perfil-header">
              <h2>Meu Perfil</h2>
              {!isEditing ? (
                <button 
                  className="btn btn-icon"
                  onClick={() => setIsEditing(true)}
                >
                  <FaEdit /> Editar
                </button>
              ) : (
                <div className="edit-actions">
                  <button 
                    className="btn btn-icon btn-success"
                    onClick={handleEditSubmit}
                  >
                    <FaSave /> Salvar
                  </button>
                  <button 
                    className="btn btn-icon btn-danger"
                    onClick={() => {
                      setIsEditing(false);
                      setEditForm({
                        nome: user.nome || '',
                        email: user.email || '',
                        telefone: user.telefone || ''
                      });
                    }}
                  >
                    <FaTimes /> Cancelar
                  </button>
                </div>
              )}
            </div>

            {editError && (
              <div className="error-message">
                {editError}
              </div>
            )}

            {isEditing ? (
              <form onSubmit={handleEditSubmit} className="edit-form">
                <div className="form-group">
                  <label htmlFor="nome">Nome</label>
                  <input
                    type="text"
                    id="nome"
                    name="nome"
                    value={editForm.nome}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={editForm.email}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="telefone">Telefone</label>
                  <input
                    type="tel"
                    id="telefone"
                    name="telefone"
                    value={editForm.telefone}
                    onChange={handleEditChange}
                    required
                    pattern="[0-9]{9,15}"
                    title="Telefone deve conter entre 9 e 15 dígitos"
                  />
                </div>
              </form>
            ) : (
              <div className="perfil-info">
                <p><strong>Nome:</strong> {user?.nome}</p>
                <p><strong>Email:</strong> {user?.email}</p>
                <p><strong>Telefone:</strong> {user?.telefone || 'Não cadastrado'}</p>
              </div>
            )}
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="loading">Carregando...</div>;
  }

  return (
    <div className="dashboard-container">
      {/* Top Navbar */}
      <header className="top-navbar">
        <div className="logo">
          <h1>AutoElite</h1>
        </div>
        <div className="nav-actions">
          <div className="notifications-container">
            <button 
              className="notification-button"
              onClick={() => setShowNotifications(!showNotifications)}
            >
              <FaBell size={20} />
              {notificacoes.filter(n => !n.lida).length > 0 && (
                <span className="notification-badge">
                  {notificacoes.filter(n => !n.lida).length}
                </span>
              )}
            </button>
            {showNotifications && (
              <div className="notifications-dropdown">
                {notificacoes.length === 0 ? (
                  <p>Nenhuma notificação</p>
                ) : (
                  notificacoes.map(notificacao => (
                    <div 
                      key={notificacao.id} 
                      className={`notification-item ${!notificacao.lida ? 'unread' : ''}`}
                    >
                      <p>{notificacao.mensagem}</p>
                      {!notificacao.lida && (
                        <button 
                          className="btn btn-text"
                          onClick={() => handleMarcarNotificacaoLida(notificacao.id)}
                        >
                          Marcar como lida
                        </button>
                      )}
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
          <div className="user-info">
            <FaUser size={20} />
            <span>{user?.nome}</span>
          </div>
        </div>
      </header>

      <div className="dashboard-wrapper">
        {/* Sidebar */}
        <nav className={`sidebar ${isSidebarCollapsed ? 'collapsed' : ''}`}>
          <button 
            className="collapse-button"
            onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
          >
            <FaBars />
          </button>
          <div className="nav-links">
            <button 
              className={`nav-link ${activeTab === 'explorar' ? 'active' : ''}`}
              onClick={() => setActiveTab('explorar')}
            >
              <FaCar />
              {!isSidebarCollapsed && <span>Explorar</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'reservas' ? 'active' : ''}`}
              onClick={() => setActiveTab('reservas')}
            >
              <FaCalendarAlt />
              {!isSidebarCollapsed && <span>Minhas Reservas</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'perfil' ? 'active' : ''}`}
              onClick={() => setActiveTab('perfil')}
            >
              <FaUserEdit />
              {!isSidebarCollapsed && <span>Meu Perfil</span>}
            </button>
            <button 
              className="nav-link logout"
              onClick={handleLogout}
            >
              <FaSignOutAlt />
              {!isSidebarCollapsed && <span>Sair</span>}
            </button>
          </div>
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {error && <div className="error-message">{error}</div>}
          {renderContent()}
        </main>
      </div>

      {/* Car Details Modal */}
      {selectedCar && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>{selectedCar.marca} {selectedCar.modelo}</h2>
            <div className="car-details">
              <p><strong>Ano:</strong> {selectedCar.ano}</p>
              <p><strong>Preço:</strong> R$ {selectedCar.preco.toLocaleString('pt-BR')}</p>
              <p><strong>Combustível:</strong> {selectedCar.combustivel}</p>
              <p><strong>Quilometragem:</strong> {selectedCar.quilometragem} km</p>
            </div>
            <div className="modal-actions">
              <button 
                className="btn btn-primary"
                onClick={() => handlePreOrder(selectedCar.id)}
              >
                Fazer Pré-reserva
              </button>
              <button 
                className="btn btn-secondary"
                onClick={() => setSelectedCar(null)}
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClienteDashboard;
