// src/components/CarGrid.js
import React from 'react';
import { FaCar } from 'react-icons/fa';

const CarGrid = ({ cars, setSelectedCar, setCurrentImageIndex, getCarImages, getApplicablePromotion, aplicarDesconto }) => {
  return (
    <div className="cars-grid">
      {cars.map(car => {
        const images = getCarImages(car.id);
        const firstImage = images.length > 0 ? images[0] : null;
        const promotion = getApplicablePromotion(car);
        const { discountedPrice, discount } = aplicarDesconto(car.preco, promotion);

        return (
          <div
            key={car.id}
            className="car-card"
            onClick={() => {
              setSelectedCar(car);
              setCurrentImageIndex(0);
            }}
          >
            {promotion && (
              <div className="promotion-title">
                <span>{promotion.titulo}</span>
              </div>
            )}
            <div className="car-image">
              {firstImage ? (
                <img src={firstImage} alt={`${car.marca} ${car.modelo}`} />
              ) : (
                <FaCar className="car-placeholder-icon" />
              )}
            </div>
            <div className="car-info">
              <h3>{car.marca} {car.modelo}</h3>
              <p>Ano: {car.ano}</p>
              {promotion ? (
                <>
                  <p className="original-price">Preço: R$ {car.preco.toLocaleString('pt-BR')}</p>
                  <p className="discount">
                    Desconto: {promotion.desconto_tipo === 'percentual'
                      ? `${promotion.desconto_valor}%`
                      : `R$ ${promotion.desconto_valor.toLocaleString('pt-BR')}`}
                  </p>
                  <p className="discounted-price">Preço Final: R$ {discountedPrice.toLocaleString('pt-BR')}</p>
                </>
              ) : (
                <p>Preço: R$ {car.preco.toLocaleString('pt-BR')}</p>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default CarGrid;
