import React, { useState, useEffect } from 'react';
import { FaCar, FaPlus, FaEdit, FaTrash, FaSearch, FaTimes, FaBars, FaSignOutAlt, FaChartBar, FaTag, FaUpload } from 'react-icons/fa';
import { adminApi } from '../../services/api';
import '../../assets/styles/AdminDashboard.css';

const AdminDashboard = ({ onNavigate }) => {
  const [activeTab, setActiveTab] = useState('veiculos');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [veiculos, setVeiculos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedVeiculo, setSelectedVeiculo] = useState(null);
  const [formData, setFormData] = useState({
    marca: '',
    modelo: '',
    ano: '',
    preco: '',
    combustivel: '',
    descricao: '',
    estoque: '',
    imagens: []
  });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    carregarVeiculos();
  }, []);

  const carregarVeiculos = async () => {
    try {
      setLoading(true);
      const response = await adminApi.listarVeiculos();
      setVeiculos(response.data.veiculos);
      setError(null);
    } catch (err) {
      setError('Erro ao carregar veículos');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    
    // Limit to 4 images
    if (files.length > 4) {
      alert('Você pode adicionar no máximo 4 imagens por veículo');
      return;
    }

    const imagePromises = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          resolve(reader.result);
        };
        reader.readAsDataURL(file);
      });
    });

    Promise.all(imagePromises).then(images => {
      // Update formData with the new images
      setFormData(prev => ({
        ...prev,
        imagens: images
      }));

      // Store images in localStorage with car details
      if (selectedVeiculo) {
        const carKey = `car_images_${selectedVeiculo.id}`;
        localStorage.setItem(carKey, JSON.stringify(images));
        
        // Update selectedVeiculo state
        setSelectedVeiculo(prev => ({
          ...prev,
          imagens: images
        }));
      }
    });
  };

  const handleRemoveImage = (index) => {
    setFormData(prev => ({
      ...prev,
      imagens: prev.imagens.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedVeiculo) {
        // Get images from localStorage
        const carKey = `car_images_${selectedVeiculo.id}`;
        const storedImages = localStorage.getItem(carKey);
        const images = storedImages ? JSON.parse(storedImages) : formData.imagens;

        const updatedFormData = {
          ...formData,
          imagens: images
        };

        const response = await adminApi.atualizarVeiculo(selectedVeiculo.id, updatedFormData);
        setShowEditModal(false);
        setSelectedVeiculo(null);
        setFormData({
          marca: '',
          modelo: '',
          ano: '',
          preco: '',
          combustivel: '',
          descricao: '',
          estoque: '',
          imagens: []
        });
        carregarVeiculos();
      } else {
        const response = await adminApi.adicionarVeiculo(formData);
        // Store images in localStorage for the new car
        if (response.data.veiculo) {
          const carKey = `car_images_${response.data.veiculo.id}`;
          localStorage.setItem(carKey, JSON.stringify(formData.imagens));
        }
        setShowAddModal(false);
        setFormData({
          marca: '',
          modelo: '',
          ano: '',
          preco: '',
          combustivel: '',
          descricao: '',
          estoque: '',
          imagens: []
        });
        carregarVeiculos();
      }
    } catch (err) {
      setError('Erro ao salvar veículo');
      console.error('Erro:', err);
    }
  };

  const handleEdit = (veiculo) => {
    setSelectedVeiculo(veiculo);
    
    // Get images from localStorage
    const carKey = `car_images_${veiculo.id}`;
    const storedImages = localStorage.getItem(carKey);
    const images = storedImages ? JSON.parse(storedImages) : (veiculo.imagens || []);

    setFormData({
      marca: veiculo.marca,
      modelo: veiculo.modelo,
      ano: veiculo.ano,
      preco: veiculo.preco,
      combustivel: veiculo.combustivel,
      descricao: veiculo.descricao,
      estoque: veiculo.estoque,
      imagens: images
    });
    setShowEditModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este veículo?')) {
      try {
        await adminApi.excluirVeiculo(id);
        carregarVeiculos();
      } catch (err) {
        setError('Erro ao excluir veículo');
        console.error('Erro:', err);
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onNavigate('landing');
  };

  const filteredVeiculos = veiculos.filter(veiculo =>
    veiculo.marca.toLowerCase().includes(searchTerm.toLowerCase()) ||
    veiculo.modelo.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'veiculos':
        return (
          <div className="admin-content">
            <div className="admin-header">
              <h1>Gerenciar Veículos</h1>
              <div className="admin-actions">
                <div className="admin-search-bar">
                  <FaSearch className="admin-search-icon" />
                  <input
                    type="text"
                    placeholder="Buscar veículos..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
                <button className="admin-btn-primary" onClick={() => setShowAddModal(true)}>
                  <FaPlus /> Adicionar Veículo
                </button>
              </div>
            </div>

            {error && <div className="admin-error-message">{error}</div>}

            <div className="admin-veiculos-grid">
              {filteredVeiculos.map(veiculo => (
                <div key={veiculo.id} className="admin-veiculo-card">
                  <div className="admin-veiculo-image">
                    {veiculo.imagens && veiculo.imagens.length > 0 ? (
                      <img src={veiculo.imagens[0]} alt={`${veiculo.marca} ${veiculo.modelo}`} />
                    ) : (
                      <FaCar className="admin-placeholder-icon" />
                    )}
                  </div>
                  <div className="admin-veiculo-info">
                    <h3>{veiculo.marca} {veiculo.modelo}</h3>
                    <p>Ano: {veiculo.ano}</p>
                    <p>Preço: R$ {veiculo.preco.toLocaleString('pt-BR')}</p>
                    <p>Estoque: {veiculo.estoque}</p>
                    <div className="admin-veiculo-actions">
                      <button className="admin-btn-edit" onClick={() => handleEdit(veiculo)}>
                        <FaEdit /> Editar
                      </button>
                      <button className="admin-btn-delete" onClick={() => handleDelete(veiculo.id)}>
                        <FaTrash /> Excluir
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'promocoes':
        return (
          <div className="admin-content">
            <h1>Gerenciar Promoções</h1>
            {/* Implementar gerenciamento de promoções */}
          </div>
        );
      case 'relatorios':
        return (
          <div className="admin-content">
            <h1>Relatórios</h1>
            {/* Implementar relatórios */}
          </div>
        );
      default:
        return null;
    }
  };

  const renderImagePreview = (images) => {
    if (!images || images.length === 0) {
      return (
        <div className="image-preview-placeholder">
          <FaCar className="admin-placeholder-icon" />
          <span>Nenhuma imagem (máximo 4)</span>
        </div>
      );
    }

    return (
      <div className="image-preview-grid">
        {images.map((image, index) => (
          <div key={index} className="image-preview-item">
            <img src={image} alt={`Preview ${index + 1}`} />
            <button
              type="button"
              className="remove-image-btn"
              onClick={() => handleRemoveImage(index)}
            >
              <FaTimes />
            </button>
          </div>
        ))}
      </div>
    );
  };

  const renderImageUploadSection = () => (
    <div className="form-group">
      <label>Imagens do Veículo (máximo 4)</label>
      <div className="image-upload-container">
        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          className="image-upload-input"
        />
        <div className="image-upload-button">
          <FaUpload /> Selecionar Imagens
        </div>
      </div>
      {renderImagePreview(formData.imagens)}
      <small className="image-upload-hint">
        Formatos aceitos: Todos. Tamanho máximo: 5MB por imagem. Máximo 4 imagens.
      </small>
    </div>
  );

  const renderModalForm = () => (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Marca</label>
        <input
          type="text"
          name="marca"
          value={formData.marca}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Modelo</label>
        <input
          type="text"
          name="modelo"
          value={formData.modelo}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Ano</label>
        <input
          type="number"
          name="ano"
          value={formData.ano}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Preço</label>
        <input
          type="number"
          name="preco"
          value={formData.preco}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="combustivel">Combustível</label>
        <input
          type="text"
          id="combustivel"
          name="combustivel"
          value={formData.combustivel}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="descricao">Descrição</label>
        <textarea
          id="descricao"
          name="descricao"
          value={formData.descricao}
          onChange={handleInputChange}
          required
        />
      </div>
      <div className="form-group">
        <label>Estoque</label>
        <input
          type="number"
          name="estoque"
          value={formData.estoque}
          onChange={handleInputChange}
          required
        />
      </div>
      {renderImageUploadSection()}
      <div className="form-actions">
        <button type="submit" className="admin-btn-primary">
          {selectedVeiculo ? 'Atualizar' : 'Adicionar'} Veículo
        </button>
      </div>
    </form>
  );

  if (loading) {
    return <div className="admin-loading">Carregando...</div>;
  }

  return (
    <div className="dashboard-container">
      {/* Top Navbar */}
      <header className="top-navbar">
        <div className="logo">
          <h1>AutoElite - Admin</h1>
        </div>
        <div className="nav-actions">
          <div className="user-info">
            <span>Administrador</span>
          </div>
        </div>
      </header>

      <div className="dashboard-wrapper">
        {/* Sidebar */}
        <nav className={`sidebar ${isSidebarCollapsed ? 'collapsed' : ''}`}>
          <button 
            className="collapse-button"
            onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
          >
            <FaBars />
          </button>
          <div className="nav-links">
            <button 
              className={`nav-link ${activeTab === 'veiculos' ? 'active' : ''}`}
              onClick={() => setActiveTab('veiculos')}
            >
              <FaCar />
              {!isSidebarCollapsed && <span>Veículos</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'promocoes' ? 'active' : ''}`}
              onClick={() => setActiveTab('promocoes')}
            >
              <FaTag />
              {!isSidebarCollapsed && <span>Promoções</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'relatorios' ? 'active' : ''}`}
              onClick={() => setActiveTab('relatorios')}
            >
              <FaChartBar />
              {!isSidebarCollapsed && <span>Relatórios</span>}
            </button>
            <button 
              className="nav-link logout"
              onClick={handleLogout}
            >
              <FaSignOutAlt />
              {!isSidebarCollapsed && <span>Sair</span>}
            </button>
          </div>
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {renderContent()}
        </main>
      </div>

      {/* Add/Edit Vehicle Modal */}
      {(showAddModal || showEditModal) && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>{showEditModal ? 'Editar Veículo' : 'Adicionar Veículo'}</h2>
              <button className="modal-close" onClick={() => {
                setShowAddModal(false);
                setShowEditModal(false);
              }}>
                <FaTimes />
              </button>
            </div>
            {renderModalForm()}
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
