import React, { useEffect, useState } from 'react';
import {
  getVehicles,
  getReservations,
  getNotifications,
  getPromocoes,
} from '../../services/api';

const ClienteDashboard = () => {
  const [vehicles, setVehicles] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [promotions, setPromotions] = useState([]);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    try {
      const [vehiclesRes, reservationsRes, notificationsRes, promotionsRes] = await Promise.all([
        getVehicles(),
        getReservations(),
        getNotifications(),
        getPromocoes(),
      ]);

      setVehicles(vehiclesRes.data);
      setReservations(reservationsRes.data);
      setNotifications(notificationsRes.data);
      setPromotions(promotionsRes.data);
    } catch (err) {
      console.error('Erro ao buscar dados:', err);
      setError(err?.mensagem || 'Erro ao carregar dados do dashboard');
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <h1>Dashboard do Cliente</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <section>
        <h2>Promoções Ativas</h2>
        {promotions.length === 0 ? (
          <p>Nenhuma promoção no momento.</p>
        ) : (
          <ul>
            {promotions.map((promo) => (
              <li key={promo.id}>
                <strong>{promo.titulo}</strong> - {promo.descricao}
                <br />
                Desconto: {promo.desconto_tipo === 'percentual'
                  ? `${promo.desconto_valor}%`
                  : `MZN ${promo.desconto_valor}`}
                <br />
                Vigência: {promo.data_inicio} até {promo.data_fim}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2>Seus Veículos</h2>
        {vehicles.length === 0 ? (
          <p>Você ainda não reservou veículos.</p>
        ) : (
          <ul>
            {vehicles.map((v) => (
              <li key={v.id}>
                {v.marca} {v.modelo} - {v.combustivel}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2>Suas Reservas</h2>
        {reservations.length === 0 ? (
          <p>Sem reservas ativas.</p>
        ) : (
          <ul>
            {reservations.map((r) => (
              <li key={r.id}>
                Veículo ID: {r.veiculo_id} | Início: {r.data_inicio} | Fim: {r.data_fim}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section>
        <h2>Notificações</h2>
        {notifications.length === 0 ? (
          <p>Sem notificações.</p>
        ) : (
          <ul>
            {notifications.map((n) => (
              <li key={n.id}>
                {n.titulo} - {n.mensagem}
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default ClienteDashboard;
