// src/components/ClienteDashboard.jsx
import React, { useState, useEffect, useRef } from 'react';
import {
  getVehicles,
  getReservations,
  getNotifications,
  getPromocoes,
  createReservation,
  cancelReservation,
  markNotificationAsRead,
  updateUserProfile,
} from '../../services/api';

const ClienteDashboard = ({ onNavigate }) => {
  // States
  const [activeTab, setActiveTab] = useState('explorar');
  const [vehicles, setVehicles] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [promotions, setPromotions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [user, setUser] = useState(null);
  const [editForm, setEditForm] = useState({ nome: '', email: '', telefone: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [editError, setEditError] = useState('');

  // Load data on mount
  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);
      setEditForm({
        nome: userData.nome || '',
        email: userData.email || '',
        telefone: userData.telefone || '',
      });
    }
    fetchData();
  }, []);

  // Fetch all necessary data
  const fetchData = async () => {
    setLoading(true);
    setError('');
    try {
      const [vehiclesRes, reservationsRes, notificationsRes, promotionsRes] =
        await Promise.all([
          getVehicles(),
          getReservations(),
          getNotifications(),
          getPromocoes(),
        ]);

      setVehicles(vehiclesRes.data.veiculos || vehiclesRes.data || []);
      setReservations(reservationsRes.data.reservas || reservationsRes.data || []);
      setNotifications(notificationsRes.data.notificacoes || notificationsRes.data || []);
      setPromotions(promotionsRes.data || []);
    } catch (err) {
      console.error(err);
      setError('Erro ao carregar dados.');
    } finally {
      setLoading(false);
    }
  };

  // Aplicar desconto de promoção no preço do veículo
  const aplicarDesconto = (precoOriginal, promocao) => {
    if (!promocao || !promocao.ativo) return precoOriginal;

    let desconto = 0;
    if (promocao.desconto_tipo === 'percentual') {
      desconto = precoOriginal * (promocao.desconto_valor / 100);
    } else if (promocao.desconto_tipo === 'valor_fixo') {
      desconto = promocao.desconto_valor;
    }
    return Math.max(precoOriginal - desconto, 0);
  };

  // Verificar promoção aplicável para um veículo
  const getPromocaoVeiculo = (veiculo) => {
    const hoje = new Date();
    return promotions.find((promo) => {
      const fim = new Date(promo.data_fim);
      if (!promo.ativo || fim < hoje) return false;

      if (promo.aplicavel_em === 'todos') return true;
      if (
        promo.aplicavel_em === 'marca' &&
        promo.marca?.toLowerCase() === veiculo.marca?.toLowerCase()
      )
        return true;
      if (
        promo.aplicavel_em === 'modelo' &&
        promo.marca?.toLowerCase() === veiculo.marca?.toLowerCase() &&
        promo.modelo?.toLowerCase() === veiculo.modelo?.toLowerCase()
      )
        return true;
      return false;
    });
  };

  // Reservar veículo
  const reservarVeiculo = async (veiculoId) => {
    try {
      await createReservation(veiculoId);
      alert('Reserva realizada com sucesso!');
      fetchData();
      setSelectedVehicle(null);
    } catch (err) {
      alert(err.response?.data?.mensagem || 'Erro ao fazer reserva');
    }
  };

  // Cancelar reserva
  const cancelarReserva = async (reservaId) => {
    try {
      await cancelReservation(reservaId);
      alert('Reserva cancelada.');
      fetchData();
    } catch (err) {
      alert(err.response?.data?.mensagem || 'Erro ao cancelar reserva');
    }
  };

  // Marcar notificação como lida
  const marcarNotificacaoLida = async (id) => {
    try {
      await markNotificationAsRead(id);
      setNotifications((prev) =>
        prev.map((n) => (n.id === id ? { ...n, lida: true } : n))
      );
    } catch (err) {
      console.error('Erro ao marcar notificação como lida:', err);
    }
  };

  // Editar perfil
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  const enviarEdicaoPerfil = async (e) => {
    e.preventDefault();
    setEditError('');
    try {
      const response = await updateUserProfile(editForm);
      alert(response.mensagem || 'Perfil atualizado com sucesso!');
      setUser({ ...user, ...editForm });
      localStorage.setItem('user', JSON.stringify({ ...user, ...editForm }));
      setIsEditing(false);
    } catch (err) {
      setEditError(err.mensagem || 'Erro ao atualizar perfil');
    }
  };

  // Renderizações
  if (loading) return <p>Carregando dados...</p>;

  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h1>Bem-vindo, {user?.nome || 'Cliente'}</h1>

      <nav style={{ marginBottom: 20 }}>
        <button onClick={() => setActiveTab('explorar')} disabled={activeTab === 'explorar'}>
          Explorar Veículos
        </button>{' '}
        <button onClick={() => setActiveTab('reservas')} disabled={activeTab === 'reservas'}>
          Minhas Reservas
        </button>{' '}
        <button onClick={() => setActiveTab('notificacoes')} disabled={activeTab === 'notificacoes'}>
          Notificações {notifications.some(n => !n.lida) ? '•' : ''}
        </button>{' '}
        <button onClick={() => setActiveTab('perfil')} disabled={activeTab === 'perfil'}>
          Meu Perfil
        </button>{' '}
        <button onClick={() => {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          onNavigate('login');
        }}>
          Sair
        </button>
      </nav>

      {activeTab === 'explorar' && (
        <>
          <h2>Veículos Disponíveis</h2>
          {vehicles.length === 0 && <p>Nenhum veículo disponível.</p>}
          <ul>
            {vehicles.map((v) => {
              const promocao = getPromocaoVeiculo(v);
              const precoComDesconto = aplicarDesconto(v.preco, promocao);

              return (
                <li key={v.id} style={{ marginBottom: 15, borderBottom: '1px solid #ccc', paddingBottom: 10 }}>
                  <strong>{v.marca} {v.modelo}</strong><br />
                  Combustível: {v.combustivel}<br />
                  Preço: <span style={{ textDecoration: promocao ? 'line-through' : 'none' }}>
                    MZN {v.preco.toFixed(2)}
                  </span>{' '}
                  {promocao && (
                    <span style={{ color: 'green', fontWeight: 'bold' }}>
                      MZN {precoComDesconto.toFixed(2)} (Promoção!)
                    </span>
                  )}<br />
                  <button onClick={() => setSelectedVehicle(v)}>Reservar</button>
                </li>
              );
            })}
          </ul>
          {selectedVehicle && (
            <div style={{ border: '1px solid #000', padding: 15, marginTop: 20 }}>
              <h3>Confirmar Reserva</h3>
              <p>Veículo: {selectedVehicle.marca} {selectedVehicle.modelo}</p>
              <button onClick={() => reservarVeiculo(selectedVehicle.id)}>Confirmar</button>{' '}
              <button onClick={() => setSelectedVehicle(null)}>Cancelar</button>
            </div>
          )}
        </>
      )}

      {activeTab === 'reservas' && (
        <>
          <h2>Minhas Reservas</h2>
          {reservations.length === 0 && <p>Você não tem reservas.</p>}
          <ul>
            {reservations.map((r) => (
              <li key={r.id} style={{ marginBottom: 10 }}>
                Veículo ID: {r.veiculo_id} | Início: {r.data_inicio} | Fim: {r.data_fim}{' '}
                <button onClick={() => cancelarReserva(r.id)}>Cancelar</button>
              </li>
            ))}
          </ul>
        </>
      )}

      {activeTab === 'notificacoes' && (
        <>
          <h2>Notificações</h2>
          {notifications.length === 0 && <p>Sem notificações.</p>}
          <ul>
            {notifications.map((n) => (
              <li key={n.id} style={{ marginBottom: 10, fontWeight: n.lida ? 'normal' : 'bold' }}>
                <p>{n.titulo}</p>
                <p>{n.mensagem}</p>
                {!n.lida && <button onClick={() => marcarNotificacaoLida(n.id)}>Marcar como lida</button>}
              </li>
            ))}
          </ul>
        </>
      )}

      {activeTab === 'perfil' && (
        <>
          <h2>Meu Perfil</h2>
          {isEditing ? (
            <form onSubmit={enviarEdicaoPerfil}>
              <div>
                <label>Nome:</label><br />
                <input
                  type="text"
                  name="nome"
                  value={editForm.nome}
                  onChange={handleEditChange}
                  required
                />
              </div>
              <div>
                <label>Email:</label><br />
                <input
                  type="email"
                  name="email"
                  value={editForm.email}
                  onChange={handleEditChange}
                  required
                />
              </div>
              <div>
                <label>Telefone:</label><br />
                <input
                  type="text"
                  name="telefone"
                  value={editForm.telefone}
                  onChange={handleEditChange}
                />
              </div>
              {editError && <p style={{ color: 'red' }}>{editError}</p>}
              <button type="submit">Salvar</button>{' '}
              <button type="button" onClick={() => setIsEditing(false)}>Cancelar</button>
            </form>
          ) : (
            <div>
              <p><strong>Nome:</strong> {user?.nome}</p>
              <p><strong>Email:</strong> {user?.email}</p>
              <p><strong>Telefone:</strong> {user?.telefone || 'Não informado'}</p>
              <button onClick={() => setIsEditing(true)}>Editar Perfil</button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ClienteDashboard;
