import React, { useState, useEffect } from 'react';
import { FaCar, FaPlus, FaEdit, FaTrash, FaSearch, FaTimes, FaBars, FaSignOutAlt, FaChartBar, FaTag } from 'react-icons/fa';
import { adminApi } from '../../services/api';
import '../../assets/styles/AdminDashboard.css';

const AdminDashboard = ({ onNavigate }) => {
  const [activeTab, setActiveTab] = useState('veiculos');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [veiculos, setVeiculos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedVeiculo, setSelectedVeiculo] = useState(null);
  const [formData, setFormData] = useState({
    marca: '',
    modelo: '',
    ano: '',
    preco: '',
    combustivel: '',
    descricao: '',
    estoque: '',
    imagens: []
  });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    carregarVeiculos();
  }, []);

  const carregarVeiculos = async () => {
    try {
      setLoading(true);
      const response = await adminApi.listarVeiculos();
      setVeiculos(response.data.veiculos);
      setError(null);
    } catch (err) {
      setError('Erro ao carregar veículos');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (event) => {
    const files = Array.from(event.target.files);
    const maxSize = 5 * 1024 * 1024; // 5MB
    const allowedTypes = ['image/jpeg', 'image/png', 'image/jpg'];

    const validFiles = files.filter(file => {
      if (!allowedTypes.includes(file.type)) {
        alert(`O arquivo ${file.name} não é um tipo de imagem válido. Use apenas JPG, JPEG ou PNG.`);
        return false;
      }
      if (file.size > maxSize) {
        alert(`O arquivo ${file.name} é muito grande. O tamanho máximo é 5MB.`);
        return false;
      }
      return true;
    });

    validFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          imagens: [...(prev.imagens || []), e.target.result]
        }));
      };
      reader.onerror = () => {
        console.error('Erro ao ler o arquivo:', file.name);
        alert(`Erro ao ler o arquivo ${file.name}. Por favor, tente novamente.`);
      };
      reader.readAsDataURL(file);
    });
  };

  const handleRemoveImage = (index) => {
    setFormData(prev => ({
      ...prev,
      imagens: prev.imagens.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Submitting form data:', formData);
      
      if (selectedVeiculo) {
        const response = await adminApi.atualizarVeiculo(selectedVeiculo.id, formData);
        console.log('Update response:', response.data);
        if (response.status === 200) {
          setShowEditModal(false);
          setSelectedVeiculo(null);
          setFormData({
            marca: '',
            modelo: '',
            ano: '',
            preco: '',
            combustivel: '',
            descricao: '',
            estoque: '',
            imagens: []
          });
          carregarVeiculos();
        }
      } else {
        const response = await adminApi.adicionarVeiculo(formData);
        console.log('Create response:', response.data);
        if (response.status === 201) {
          setShowAddModal(false);
          setFormData({
            marca: '',
            modelo: '',
            ano: '',
            preco: '',
            combustivel: '',
            descricao: '',
            estoque: '',
            imagens: []
          });
          carregarVeiculos();
        }
      }
    } catch (error) {
      console.error('Erro:', error);
      console.error('Error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      alert(error.response?.data?.mensagem || 'Erro ao salvar veículo');
    }
  };

  const handleEdit = (veiculo) => {
    setSelectedVeiculo(veiculo);
    setFormData({
      marca: veiculo.marca,
      modelo: veiculo.modelo,
      ano: veiculo.ano,
      preco: veiculo.preco,
      combustivel: veiculo.combustivel,
      descricao: veiculo.descricao,
      estoque: veiculo.estoque,
      imagens: veiculo.imagens || []
    });
    setShowEditModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este veículo?')) {
      try {
        await adminApi.deletarVeiculo(id);
        carregarVeiculos();
      } catch (err) {
        setError('Erro ao deletar veículo');
        console.error('Erro:', err);
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onNavigate('landing');
  };

  const filteredVeiculos = veiculos.filter(veiculo =>
    veiculo.marca.toLowerCase().includes(searchTerm.toLowerCase()) ||
    veiculo.modelo.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const renderContent = () => {
    switch (activeTab) {
      case 'veiculos':
        return (
          <div className="admin-content">
            <div className="admin-header">
              <h1>Gerenciar Veículos</h1>
              <div className="admin-actions">
                <div className="admin-search-bar">
                  <FaSearch className="admin-search-icon" />
                  <input
                    type="text"
                    placeholder="Buscar veículos..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
                <button className="admin-btn-primary" onClick={() => setShowAddModal(true)}>
                  <FaPlus /> Adicionar Veículo
                </button>
              </div>
            </div>

            {error && <div className="admin-error-message">{error}</div>}

            <div className="admin-veiculos-grid">
              {filteredVeiculos.map(veiculo => (
                <div key={veiculo.id} className="admin-veiculo-card">
                  <div className="admin-veiculo-image">
                    {veiculo.imagens && veiculo.imagens.length > 0 ? (
                      <img src={veiculo.imagens[0]} alt={`${veiculo.marca} ${veiculo.modelo}`} />
                    ) : (
                      <FaCar className="admin-placeholder-icon" />
                    )}
                  </div>
                  <div className="admin-veiculo-info">
                    <h3>{veiculo.marca} {veiculo.modelo}</h3>
                    <p>Ano: {veiculo.ano}</p>
                    <p>Preço: R$ {veiculo.preco.toLocaleString('pt-BR')}</p>
                    <p>Estoque: {veiculo.estoque}</p>
                    <div className="admin-veiculo-actions">
                      <button className="admin-btn-edit" onClick={() => handleEdit(veiculo)}>
                        <FaEdit /> Editar
                      </button>
                      <button className="admin-btn-delete" onClick={() => handleDelete(veiculo.id)}>
                        <FaTrash /> Excluir
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'promocoes':
        return (
          <div className="admin-content">
            <h1>Gerenciar Promoções</h1>
            {/* Implementar gerenciamento de promoções */}
          </div>
        );
      case 'relatorios':
        return (
          <div className="admin-content">
            <h1>Relatórios</h1>
            {/* Implementar relatórios */}
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="admin-loading">Carregando...</div>;
  }

  return (
    <div className="dashboard-container">
      {/* Top Navbar */}
      <header className="top-navbar">
        <div className="logo">
          <h1>AutoElite - Admin</h1>
        </div>
        <div className="nav-actions">
          <div className="user-info">
            <span>Administrador</span>
          </div>
        </div>
      </header>

      <div className="dashboard-wrapper">
        {/* Sidebar */}
        <nav className={`sidebar ${isSidebarCollapsed ? 'collapsed' : ''}`}>
          <button 
            className="collapse-button"
            onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
          >
            <FaBars />
          </button>
          <div className="nav-links">
            <button 
              className={`nav-link ${activeTab === 'veiculos' ? 'active' : ''}`}
              onClick={() => setActiveTab('veiculos')}
            >
              <FaCar />
              {!isSidebarCollapsed && <span>Veículos</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'promocoes' ? 'active' : ''}`}
              onClick={() => setActiveTab('promocoes')}
            >
              <FaTag />
              {!isSidebarCollapsed && <span>Promoções</span>}
            </button>
            <button 
              className={`nav-link ${activeTab === 'relatorios' ? 'active' : ''}`}
              onClick={() => setActiveTab('relatorios')}
            >
              <FaChartBar />
              {!isSidebarCollapsed && <span>Relatórios</span>}
            </button>
            <button 
              className="nav-link logout"
              onClick={handleLogout}
            >
              <FaSignOutAlt />
              {!isSidebarCollapsed && <span>Sair</span>}
            </button>
          </div>
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {renderContent()}
        </main>
      </div>

      {/* Add/Edit Vehicle Modal */}
      {(showAddModal || showEditModal) && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>{showEditModal ? 'Editar Veículo' : 'Adicionar Veículo'}</h2>
              <button className="modal-close" onClick={() => {
                setShowAddModal(false);
                setShowEditModal(false);
              }}>
                <FaTimes />
              </button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Marca</label>
                <input
                  type="text"
                  name="marca"
                  value={formData.marca}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Modelo</label>
                <input
                  type="text"
                  name="modelo"
                  value={formData.modelo}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Ano</label>
                <input
                  type="number"
                  name="ano"
                  value={formData.ano}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Preço</label>
                <input
                  type="number"
                  name="preco"
                  value={formData.preco}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="combustivel">Combustível</label>
                <input
                  type="text"
                  id="combustivel"
                  name="combustivel"
                  value={formData.combustivel}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="descricao">Descrição</label>
                <textarea
                  id="descricao"
                  name="descricao"
                  value={formData.descricao}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Estoque</label>
                <input
                  type="number"
                  name="estoque"
                  value={formData.estoque}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Imagens</label>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageUpload}
                />
                <div className="image-preview-grid">
                  {formData.imagens && formData.imagens.map((image, index) => (
                    <div key={index} className="image-preview-item">
                      <img src={image} alt={`Preview ${index + 1}`} />
                      <button
                        type="button"
                        className="remove-image-btn"
                        onClick={() => handleRemoveImage(index)}
                      >
                        <FaTimes />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
              <div className="admin-form-actions">
                <button type="submit" className="admin-btn-primary">
                  {showEditModal ? 'Salvar Alterações' : 'Adicionar Veículo'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
