import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Forum = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newMessage, setNewMessage] = useState('');
  const [newImage, setNewImage] = useState(null);
  const [selectedPost, setSelectedPost] = useState(null);
  const [replies, setReplies] = useState([]);
  const [replyMessage, setReplyMessage] = useState('');
  const [replyImage, setReplyImage] = useState(null);
  const [replyLoading, setReplyLoading] = useState(false);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/forum/posts');
      setPosts(res.data.posts);
      setError('');
    } catch (err) {
      setError('Erro ao carregar posts');
    } finally {
      setLoading(false);
    }
  };

  const handleNewPost = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    let imagem_url = null;
    if (newImage) {
      // For demo: store as base64. In prod, upload to server/static and store URL.
      const reader = new FileReader();
      reader.onload = async (ev) => {
        imagem_url = ev.target.result;
        await sendPost(imagem_url);
      };
      reader.readAsDataURL(newImage);
    } else {
      await sendPost(null);
    }
  };

  const sendPost = async (imagem_url) => {
    try {
      await axios.post('/api/forum/posts', { mensagem: newMessage, imagem_url });
      setNewMessage('');
      setNewImage(null);
      fetchPosts();
    } catch (err) {
      setError('Erro ao criar post');
    }
  };

  const handleSelectPost = async (post) => {
    setSelectedPost(post);
    setReplyMessage('');
    setReplyImage(null);
    setReplyLoading(true);
    try {
      const res = await axios.get(`/api/forum/posts/${post.id}/respostas`);
      setReplies(res.data.respostas);
    } catch {
      setReplies([]);
    } finally {
      setReplyLoading(false);
    }
  };

  const handleReply = async (e) => {
    e.preventDefault();
    if (!replyMessage.trim()) return;
    let imagem_url = null;
    if (replyImage) {
      const reader = new FileReader();
      reader.onload = async (ev) => {
        imagem_url = ev.target.result;
        await sendReply(imagem_url);
      };
      reader.readAsDataURL(replyImage);
    } else {
      await sendReply(null);
    }
  };

  const sendReply = async (imagem_url) => {
    try {
      await axios.post(`/api/forum/posts/${selectedPost.id}/respostas`, { resposta: replyMessage, imagem_url });
      setReplyMessage('');
      setReplyImage(null);
      handleSelectPost(selectedPost);
    } catch (err) {
      setError('Erro ao responder');
    }
  };

  return (
    <div className="forum-section">
      <h2>Fórum</h2>
      {error && <div className="error-message">{error}</div>}
      <form className="forum-new-post" onSubmit={handleNewPost}>
        <textarea
          placeholder="Escreva sua dúvida ou mensagem..."
          value={newMessage}
          onChange={e => setNewMessage(e.target.value)}
          required
        />
        <input type="file" accept="image/*" onChange={e => setNewImage(e.target.files[0])} />
        <button type="submit">Enviar</button>
      </form>
      {loading ? (
        <div>Carregando posts...</div>
      ) : selectedPost ? (
        <div className="forum-thread">
          <button onClick={() => setSelectedPost(null)}>Voltar</button>
          <div className="forum-post">
            <div><strong>{selectedPost.usuario_nome}</strong> <span>({new Date(selectedPost.data_postagem).toLocaleString('pt-BR')})</span></div>
            <div>{selectedPost.mensagem}</div>
            {selectedPost.imagem_url && <img src={selectedPost.imagem_url} alt="imagem" style={{maxWidth:200}} />}
          </div>
          <div className="forum-replies">
            <h4>Respostas</h4>
            {replyLoading ? <div>Carregando respostas...</div> : replies.length === 0 ? <div>Nenhuma resposta ainda.</div> : replies.map(r => (
              <div key={r.id} className="forum-reply">
                <div><strong>{r.usuario_nome}</strong> <span>({new Date(r.data_resposta).toLocaleString('pt-BR')})</span></div>
                <div>{r.resposta}</div>
                {r.imagem_url && <img src={r.imagem_url} alt="imagem" style={{maxWidth:150}} />}
              </div>
            ))}
          </div>
          <form className="forum-reply-form" onSubmit={handleReply}>
            <textarea
              placeholder="Responder..."
              value={replyMessage}
              onChange={e => setReplyMessage(e.target.value)}
              required
            />
            <input type="file" accept="image/*" onChange={e => setReplyImage(e.target.files[0])} />
            <button type="submit">Responder</button>
          </form>
        </div>
      ) : (
        <div className="forum-posts-list">
          {posts.length === 0 ? <div>Nenhuma mensagem no fórum ainda.</div> : posts.map(post => (
            <div key={post.id} className="forum-post-list-item" onClick={() => handleSelectPost(post)}>
              <div><strong>{post.usuario_nome}</strong> <span>({new Date(post.data_postagem).toLocaleString('pt-BR')})</span></div>
              <div>{post.mensagem}</div>
              {post.imagem_url && <img src={post.imagem_url} alt="imagem" style={{maxWidth:100}} />}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Forum;
