// src/components/CarModal.js
import React from 'react';
import { FaChevronLeft, FaChevronRight, FaCar } from 'react-icons/fa';

const CarModal = ({ selectedCar, setSelectedCar, currentImageIndex, setCurrentImageIndex, getCarImages, handlePreOrder }) => {
  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % getCarImages(selectedCar?.id).length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + getCarImages(selectedCar?.id).length) % getCarImages(selectedCar?.id).length);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content car-modal">
        <div className="car-gallery">
          <button className="gallery-nav prev" onClick={prevImage}>
            <FaChevronLeft />
          </button>
          {getCarImages(selectedCar.id).length > 0 ? (
            <img
              src={getCarImages(selectedCar.id)[currentImageIndex]}
              alt={`${selectedCar.marca} ${selectedCar.modelo}`}
              className="car-gallery-image"
            />
          ) : (
            <div className="car-gallery-placeholder">
              <FaCar size={40} />
              <p>Nenhuma imagem dispon√≠vel</p>
            </div>
          )}
          <button className="gallery-nav next" onClick={nextImage}>
            <FaChevronRight />
          </button>
          <div className="gallery-dots">
            {getCarImages(selectedCar.id).map((_, index) => (
              <span
                key={index}
                className={`dot ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
              />
            ))}
          </div>
        </div>
        <div className="car-details">
          <h2>{selectedCar.marca} {selectedCar.modelo}</h2>
          <div className="car-specs">
            <p><strong>Ano:</strong> {selectedCar.ano}</p>
            <p><strong>Pre√ßo:</strong> R$ {selectedCar.preco.toLocaleString('pt-BR')}</p>
            <p><strong>Combust√≠vel:</strong> {selectedCar.combustivel}</p>
            <p><strong>Quilometragem:</strong> {selectedCar.quilometragem} km</p>
          </div>
          <div className="modal-actions">
            <button
              className="btn btn-primary"
              onClick={() => handlePreOrder(selectedCar.id)}
            >
              Fazer Pr√©-reserva
            </button>
            <button
              className="btn btn-secondary"
              onClick={() => setSelectedCar(null)}
            >
              Fechar
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CarModal;
