import React, { useState, useEffect } from 'react';
import { FaSearch, FaEdit, FaTrash } from 'react-icons/fa';
import { adminApi } from '../../services/api';
import '../../assets/styles/AdminDashboard.css';

const Usuario = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedUsuario, setSelectedUsuario] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    telefone: '',
    role: 'cliente',
  });

  useEffect(() => {
    carregarUsuarios();
  }, []);

  useEffect(() => {
    if (error) {
      const timer = setTimeout(() => setError(null), 5000);
      return () => clearTimeout(timer);
    }
  }, [error]);

  const carregarUsuarios = async () => {
    try {
      setLoading(true);
      const response = await adminApi.listarUsuarios();
      setUsuarios(response.data.usuarios || response.data);
    } catch (err) {
      setError('Erro ao carregar usuários');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleEdit = (usuario) => {
    setSelectedUsuario(usuario);
    setFormData({
      nome: usuario.nome,
      email: usuario.email,
      telefone: usuario.telefone,
      role: usuario.role || 'cliente',
    });
    setShowEditModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setActionLoading(true);

    if (!formData.nome || !formData.email || !formData.telefone) {
      setError('Todos os campos são obrigatórios');
      setActionLoading(false);
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('E-mail inválido');
      setActionLoading(false);
      return;
    }

    const telefoneRegex = /^\d{9,15}$/;
    if (!telefoneRegex.test(formData.telefone)) {
      setError('Telefone inválido (9 a 15 dígitos)');
      setActionLoading(false);
      return;
    }

    if (!['admin', 'cliente'].includes(formData.role)) {
      setError('Role inválida');
      setActionLoading(false);
      return;
    }

    try {
      await adminApi.atualizarDadosPessoais(selectedUsuario.id, formData);
      setShowEditModal(false);
      setSelectedUsuario(null);
      setFormData({ nome: '', email: '', telefone: '', role: 'cliente' });
      carregarUsuarios();
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao atualizar usuário');
      console.error(err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Tem certeza que deseja excluir este usuário?')) return;
    setActionLoading(true);
    try {
      await adminApi.excluirUsuario(id);
      carregarUsuarios();
    } catch (err) {
      setError('Erro ao excluir usuário');
      console.error(err);
    } finally {
      setActionLoading(false);
    }
  };

  const filteredUsuarios = usuarios.filter(
    u => u.nome.toLowerCase().includes(searchTerm.toLowerCase()) || u.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <div>Carregando...</div>;

  return (
    <div className="admin-content">
      <h1>Gerenciar Usuários</h1>
      <div className="search-bar">
        <FaSearch />
        <input
          type="text"
          placeholder="Buscar por nome ou email..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="user-list">
        {filteredUsuarios.map(usuario => (
          <div key={usuario.id} className="user-card">
            <h3>{usuario.nome}</h3>
            <p>Email: {usuario.email}</p>
            <p>Telefone: {usuario.telefone || 'Não informado'}</p>
            <p>Role: {usuario.role === 'admin' ? 'Administrador' : 'Cliente'}</p>
            <button onClick={() => handleEdit(usuario)} disabled={actionLoading}>
              <FaEdit /> Editar
            </button>
            <button onClick={() => handleDelete(usuario.id)} disabled={actionLoading}>
              <FaTrash /> Excluir
            </button>
          </div>
        ))}
      </div>

      {showEditModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Editar Usuário</h2>
            <form onSubmit={handleSubmit}>
              <label>Nome</label>
              <input name="nome" value={formData.nome} onChange={handleInputChange} required />
              <label>Email</label>
              <input name="email" type="email" value={formData.email} onChange={handleInputChange} required />
              <label>Telefone</label>
              <input name="telefone" value={formData.telefone} onChange={handleInputChange} required />
              <label>Role</label>
              <select name="role" value={formData.role} onChange={handleInputChange} required>
                <option value="cliente">Cliente</option>
                <option value="admin">Administrador</option>
              </select>
              {error && <div className="error-message">{error}</div>}
              <button type="submit" disabled={actionLoading}>
                {actionLoading ? 'Salvando...' : 'Salvar'}
              </button>
              <button
                type="button"
                onClick={() => {
                  setShowEditModal(false);
                  setSelectedUsuario(null);
                  setError(null);
                  setFormData({ nome: '', email: '', telefone: '', role: 'cliente' });
                }}
                disabled={actionLoading}
              >
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Usuario;
