// src/components/ProfileSection.js
import React from 'react';
import { FaSave, FaTimes, FaEdit, FaCamera, FaUserCircle } from 'react-icons/fa';

const ProfileSection = ({
  user,
  isEditing,
  setIsEditing,
  editForm,
  setEditForm,
  editError,
  handleEditSubmit,
  handleEditChange,
  profileImage,
  isUploading,
  triggerImageUpload,
  fileInputRef,
}) => {

  // ✅ Função para lidar com upload de imagem
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      console.log('Imagem selecionada:', file);
      // Aqui você pode adicionar lógica para enviar para o servidor, por exemplo:
      // const formData = new FormData();
      // formData.append('image', file);
      // fetch('/api/upload', { method: 'POST', body: formData });
    }
  };

  return (
    <div className="perfil-section">
      <div className="perfil-header">
        <h2>Meu Perfil</h2>
        <div className="edit-actions">
          {isEditing ? (
            <>
              <button className="btn-icon btn-success" onClick={handleEditSubmit}>
                <FaSave /> Salvar
              </button>
              <button
                className="btn-icon btn-danger"
                onClick={() => {
                  setIsEditing(false);
                  setEditForm({
                    nome: user?.nome || '',
                    email: user?.email || '',
                    telefone: user?.telefone || ''
                  });
                }}
              >
                <FaTimes /> Cancelar
              </button>
            </>
          ) : (
            <button className="btn-icon" onClick={() => setIsEditing(true)}>
              <FaEdit /> Editar
            </button>
          )}
        </div>
      </div>

      <div className="perfil-content">
        <div className="perfil-image-container">
          {profileImage ? (
            <img
              src={profileImage}
              alt="Foto de perfil"
              className="perfil-image"
            />
          ) : (
            <div className="perfil-image-placeholder">
              <FaUserCircle size={80} />
            </div>
          )}
          <button
            className="btn-upload"
            onClick={triggerImageUpload}
            disabled={isUploading}
          >
            <FaCamera />
            {isUploading ? 'Enviando...' : 'Alterar foto'}
          </button>
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleImageUpload}
            accept="image/*"
            style={{ display: 'none' }}
          />
        </div>

        {isEditing ? (
          <form onSubmit={handleEditSubmit} className="edit-form">
            <div className="form-group">
              <label htmlFor="nome">Nome Completo</label>
              <input
                type="text"
                id="nome"
                name="nome"
                value={editForm.nome}
                onChange={handleEditChange}
                required
                placeholder="Digite seu nome completo"
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={editForm.email}
                onChange={handleEditChange}
                required
                placeholder="Digite seu email"
              />
            </div>
            <div className="form-group">
              <label htmlFor="telefone">Telefone</label>
              <input
                type="tel"
                id="telefone"
                name="telefone"
                value={editForm.telefone}
                onChange={handleEditChange}
                required
                pattern="[0-9]{9,15}"
                title="Telefone deve conter entre 9 e 15 dígitos"
                placeholder="Digite seu telefone"
              />
            </div>
            {editError && <div className="error-message">{editError}</div>}
          </form>
        ) : (
          <div className="perfil-info">
            <div className="info-group">
              <label>Nome Completo</label>
              <p>{user?.nome || 'Não informado'}</p>
            </div>
            <div className="info-group">
              <label>Email</label>
              <p>{user?.email || 'Não informado'}</p>
            </div>
            <div className="info-group">
              <label>Telefone</label>
              <p>{user?.telefone || 'Não informado'}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileSection;
