// src/components/ClienteDashboard.js
import React, { useState, useEffect, useRef } from 'react';
import { getVehicles, getReservations, getNotifications, markNotificationAsRead, createReservation, cancelReservation, updateUserProfile } from '../../services/api';
import TopNavbar from './TopNavbar';
import Sidebar from './Sidebar';
import CarGrid from './CarGrid';
import ReservationsGrid from './ReservationsGrid';
import ProfileSection from './ProfileSection';
import CarModal from './CarModal';
import '../../assets/styles/ClienteDashboard.css';

const ClienteDashboard = ({ onNavigate }) => {
  const [activeTab, setActiveTab] = useState('explorar');
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [cars, setCars] = useState([]);
  const [reservations, setReservations] = useState([]);
  const [notificacoes, setNotificacoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showNotifications, setShowNotifications] = useState(false);
  const [selectedCar, setSelectedCar] = useState(null);
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    nome: '',
    email: '',
    telefone: ''
  });
  const [editError, setEditError] = useState('');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [profileImage, setProfileImage] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef(null);

  useEffect(() => {
    fetchData();
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);
      setEditForm({
        nome: userData.nome || '',
        email: userData.email || '',
        telefone: userData.telefone || ''
      });
      const savedImage = localStorage.getItem(`profileImage_${userData.email}`);
      if (savedImage) {
        setProfileImage(savedImage);
      }
    }
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [vehiclesRes, notificationsRes, reservationsRes] = await Promise.all([
        getVehicles(),
        getNotifications(),
        getReservations()
      ]);

      if (vehiclesRes.data.veiculos) {
        setCars(vehiclesRes.data.veiculos);
      }
      if (notificationsRes.data.notificacoes) {
        setNotificacoes(notificationsRes.data.notificacoes);
      }
      if (reservationsRes.data.reservas) {
        setReservations(reservationsRes.data.reservas);
      }
    } catch (err) {
      setError('Erro ao carregar dados');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleMarcarNotificacaoLida = async (notificacaoId) => {
    try {
      await markNotificationAsRead(notificacaoId);
      setNotificacoes(notificacoes.map(notificacao =>
        notificacao.id === notificacaoId
          ? { ...notificacao, lida: true }
          : notificacao
      ));
    } catch (err) {
      console.error('Erro ao marcar notificação como lida:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onNavigate('landing');
  };

  const handlePreOrder = async (carId) => {
    try {
      const response = await createReservation(carId);
      if (response.data) {
        setSelectedCar(null);
        fetchData();
      }
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao fazer pré-reserva');
    }
  };

  const handleCancelReservation = async (reservaId) => {
    try {
      await cancelReservation(reservaId);
      fetchData();
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao cancelar reserva');
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setEditError('');

    try {
      const response = await updateUserProfile(editForm);
      if (response.mensagem === 'Dados pessoais atualizados com sucesso') {
        const updatedUser = { ...user, ...editForm };
        localStorage.setItem('user', JSON.stringify(updatedUser));
        setUser(updatedUser);
        setIsEditing(false);
      }
    } catch (err) {
      setEditError(err.mensagem || 'Erro ao atualizar perfil');
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const compressImage = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = (event) => {
        const img = new Image();
        img.src = event.target.result;
        img.onload = () => {
          const canvas = document.createElement('canvas');
          const MAX_WIDTH = 400;
          const MAX_HEIGHT = 400;
          let width = img.width;
          let height = img.height;

          if (width > height) {
            if (width > MAX_WIDTH) {
              height *= MAX_WIDTH / width;
              width = MAX_WIDTH;
            }
          } else {
            if (height > MAX_HEIGHT) {
              width *= MAX_HEIGHT / height;
              height = MAX_HEIGHT;
            }
          }

          canvas.width = width;
          canvas.height = height;
          const ctx = canvas.getContext('2d');
          ctx.drawImage(img, 0, 0, width, height);
          const compressedDataUrl = canvas.toDataURL('image/jpeg', 0.7);
          resolve(compressedDataUrl);
        };
        img.onerror = reject;
      };
      reader.onerror = reject;
    });
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      try {
        setIsUploading(true);
        const compressedImage = await compressImage(file);
        setProfileImage(compressedImage);
        localStorage.setItem(`profileImage_${user.email}`, compressedImage);
      } catch (error) {
        console.error('Error processing image:', error);
        alert('Erro ao processar a imagem. Por favor, tente novamente com uma imagem menor.');
      } finally {
        setIsUploading(false);
      }
    }
  };

  const triggerImageUpload = () => {
    fileInputRef.current.click();
  };

  const getCarImages = (carId) => {
    const carKey = `car_images_${carId}`;
    const storedImages = localStorage.getItem(carKey);
    if (storedImages) {
      return JSON.parse(storedImages);
    }
    return [];
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'explorar':
        return (
          <CarGrid
            cars={cars}
            setSelectedCar={setSelectedCar}
            setCurrentImageIndex={setCurrentImageIndex}
            getCarImages={getCarImages}
          />
        );
      case 'reservas':
        return (
          <ReservationsGrid
            reservations={reservations}
            handleCancelReservation={handleCancelReservation}
            getCarImages={getCarImages}
          />
        );
      case 'perfil':
        return (
          <ProfileSection
            user={user}
            isEditing={isEditing}
            setIsEditing={setIsEditing}
            editForm={editForm}
            setEditForm={setEditForm}
            editError={editError}
            handleEditSubmit={handleEditSubmit}
            handleEditChange={handleEditChange}
            profileImage={profileImage}
            isUploading={isUploading}
            triggerImageUpload={triggerImageUpload}
            fileInputRef={fileInputRef}
          />
        );
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="loading">Carregando...</div>;
  }

  return (
    <div className="dashboard-container">
      <TopNavbar
        notificacoes={notificacoes}
        showNotifications={showNotifications}
        setShowNotifications={setShowNotifications}
        handleMarcarNotificacaoLida={handleMarcarNotificacaoLida}
        user={user}
        profileImage={profileImage}
      />
      <div className="dashboard-wrapper">
        <Sidebar
          activeTab={activeTab}
          setActiveTab={setActiveTab}
          isSidebarCollapsed={isSidebarCollapsed}
          setIsSidebarCollapsed={setIsSidebarCollapsed}
          handleLogout={handleLogout}
        />
        <main className="main-content">
          {error && <div className="error-message">{error}</div>}
          {renderContent()}
        </main>
      </div>
      {selectedCar && (
        <CarModal
          selectedCar={selectedCar}
          setSelectedCar={setSelectedCar}
          currentImageIndex={currentImageIndex}
          setCurrentImageIndex={setCurrentImageIndex}
          getCarImages={getCarImages}
          handlePreOrder={handlePreOrder}
        />
      )}
    </div>
  );
};

export default ClienteDashboard;
