import React, { useState, useEffect } from 'react';
import { FaSearch, FaUsers, FaEdit, FaTrash } from 'react-icons/fa';
import { adminApi } from '../../services/api';
import '../../assets/styles/AdminDashboard.css';

const Usuarios = ({ activeTab, setActiveTab }) => {
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedUsuario, setSelectedUsuario] = useState(null);
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    telefone: '',
  });

  useEffect(() => {
    carregarUsuarios();
  }, []);

  const carregarUsuarios = async () => {
    try {
      setLoading(true);
      const response = await adminApi.listarUsuarios();
      setUsuarios(response.data.usuarios);
      setError(null);
    } catch (err) {
      setError('Erro ao carregar usuários');
      console.error('Erro:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleEdit = (usuario) => {
    setSelectedUsuario(usuario);
    setFormData({
      nome: usuario.nome,
      email: usuario.email,
      telefone: usuario.telefone,
    });
    setShowEditModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.nome || !formData.email || !formData.telefone) {
      setError('Nome, e-mail e telefone são obrigatórios');
      return;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('E-mail inválido');
      return;
    }
    const telefoneRegex = /^\d{9,15}$/;
    if (!telefoneRegex.test(formData.telefone)) {
      setError('Telefone inválido (deve conter entre 9 e 15 dígitos)');
      return;
    }
    try {
      await adminApi.atualizarDadosPessoais(selectedUsuario.id, formData);
      setShowEditModal(false);
      setSelectedUsuario(null);
      setFormData({ nome: '', email: '', telefone: '' });
      setError(null);
      carregarUsuarios();
    } catch (err) {
      setError(err.response?.data?.mensagem || 'Erro ao atualizar usuário');
      console.error('Erro:', err);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este usuário?')) {
      try {
        await adminApi.excluirUsuario(id);
        carregarUsuarios();
      } catch (err) {
        setError(err.response?.data?.mensagem || 'Erro ao excluir usuário');
        console.error('Erro:', err);
      }
    }
  };

  const filteredUsuarios = usuarios.filter(
    (usuario) =>
      usuario.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
      usuario.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return <div className="admin-loading">Carregando...</div>;
  }

  return (
    <div className="admin-content">
      <div className="admin-header">
        <h1>Gerenciar Usuários</h1>
        <div className="admin-actions">
          <div className="admin-search-bar">
            <FaSearch className="admin-search-icon" />
            <input
              type="text"
              placeholder="Buscar usuários..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>
      {error && <div className="admin-error-message">{error}</div>}
      <div className="admin-usuarios-grid">
        {filteredUsuarios.map((usuario) => (
          <div key={usuario.id} className="admin-usuario-card">
            <div className="admin-usuario-info">
              <h3>{usuario.nome}</h3>
              <p>Email: {usuario.email}</p>
              <p>Telefone: {usuario.telefone}</p>
              <div className="admin-usuario-actions">
                <button
                  className="admin-btn-edit"
                  onClick={() => handleEdit(usuario)}
                >
                  <FaEdit /> Editar
                </button>
                <button
                  className="admin-btn-delete"
                  onClick={() => handleDelete(usuario.id)}
                >
                  <FaTrash /> Excluir
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {showEditModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Editar Usuário</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Nome</label>
                <input
                  type="text"
                  name="nome"
                  value={formData.nome}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Telefone</label>
                <input
                  type="text"
                  name="telefone"
                  value={formData.telefone}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="modal-actions">
                <button type="submit" className="admin-btn-primary">
                  Salvar
                </button>
                <button
                  type="button"
                  className="admin-btn-secondary"
                  onClick={() => {
                    setShowEditModal(false);
                    setSelectedUsuario(null);
                    setFormData({ nome: '', email: '', telefone: '' });
                    setError(null);
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Usuarios;
