import React, { useState, useEffect } from 'react';

const ClienteDashboard = ({ onNavigate }) => {
  const [reservas, setReservas] = useState([]);
  const [notificacoes, setNotificacoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('reservas');
  const [profileData, setProfileData] = useState({
    nome: '',
    email: '',
    telefone: ''
  });

  useEffect(() => {
    fetchReservas();
    fetchNotificacoes();
    fetchProfile();
  }, []);

  const fetchReservas = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/reservas', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setReservas(data.reservas);
      } else {
        setError(data.mensagem);
      }
    } catch (err) {
      setError('Erro ao carregar reservas');
    }
  };

  const fetchNotificacoes = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/notificacoes', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setNotificacoes(data.notificacoes);
      }
    } catch (err) {
      console.error('Erro ao carregar notificações:', err);
    }
  };

  const fetchProfile = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      if (user) {
        setProfileData({
          nome: user.nome || '',
          email: user.email || '',
          telefone: user.telefone || ''
        });
      }
    } catch (err) {
      console.error('Erro ao carregar perfil:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelarReserva = async (reservaId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:3001/api/reservas/${reservaId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        fetchReservas();
        fetchNotificacoes();
      } else {
        setError(data.mensagem);
      }
    } catch (err) {
      setError('Erro ao cancelar reserva');
    }
  };

  const handleMarcarNotificacaoLida = async (notificacaoId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:3001/api/notificacoes/${notificacaoId}/lida`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        fetchNotificacoes();
      }
    } catch (err) {
      console.error('Erro ao marcar notificação como lida:', err);
    }
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:3001/api/usuarios/perfil', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(profileData)
      });
      const data = await response.json();
      if (response.ok) {
        setError('');
        // Update local storage
        const user = JSON.parse(localStorage.getItem('user'));
        localStorage.setItem('user', JSON.stringify({ ...user, ...profileData }));
      } else {
        setError(data.mensagem);
      }
    } catch (err) {
      setError('Erro ao atualizar perfil');
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pendente': return 'var(--warning-color)';
      case 'confirmada': return 'var(--success-color)';
      case 'cancelada': return 'var(--error-color)';
      default: return 'var(--text-light)';
    }
  };

  if (loading) {
    return <div className="loading">Carregando...</div>;
  }

  return (
    <div className="dashboard">
      <nav className="dashboard-nav">
        <button 
          className={`nav-tab ${activeTab === 'reservas' ? 'active' : ''}`}
          onClick={() => setActiveTab('reservas')}
        >
          Minhas Reservas
        </button>
        <button 
          className={`nav-tab ${activeTab === 'notificacoes' ? 'active' : ''}`}
          onClick={() => setActiveTab('notificacoes')}
        >
          Notificações
          {notificacoes.filter(n => !n.lida).length > 0 && (
            <span className="notification-badge">
              {notificacoes.filter(n => !n.lida).length}
            </span>
          )}
        </button>
        <button 
          className={`nav-tab ${activeTab === 'perfil' ? 'active' : ''}`}
          onClick={() => setActiveTab('perfil')}
        >
          Meu Perfil
        </button>
      </nav>

      <div className="dashboard-content">
        {error && <div className="error-message">{error}</div>}

        {activeTab === 'reservas' && (
          <div className="reservas-section">
            <h2>Minhas Reservas</h2>
            {reservas.length === 0 ? (
              <p className="no-data">Você ainda não tem reservas.</p>
            ) : (
              <div className="reservas-grid">
                {reservas.map(reserva => (
                  <div key={reserva.id} className="reserva-card">
                    <div className="reserva-header">
                      <h3>{reserva.marca} {reserva.modelo}</h3>
                      <span 
                        className="status-badge"
                        style={{ backgroundColor: getStatusColor(reserva.status) }}
                      >
                        {reserva.status}
                      </span>
                    </div>
                    <div className="reserva-details">
                      <p>Ano: {reserva.ano}</p>
                      <p>Preço: R$ {reserva.preco.toLocaleString('pt-BR')}</p>
                      <p>Data: {new Date(reserva.data_reserva).toLocaleDateString('pt-BR')}</p>
                    </div>
                    {reserva.status === 'pendente' && (
                      <button 
                        className="btn btn-danger"
                        onClick={() => handleCancelarReserva(reserva.id)}
                      >
                        Cancelar Reserva
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'notificacoes' && (
          <div className="notificacoes-section">
            <h2>Notificações</h2>
            {notificacoes.length === 0 ? (
              <p className="no-data">Nenhuma notificação.</p>
            ) : (
              <div className="notificacoes-list">
                {notificacoes.map(notificacao => (
                  <div 
                    key={notificacao.id} 
                    className={`notificacao-item ${!notificacao.lida ? 'unread' : ''}`}
                  >
                    <p>{notificacao.mensagem}</p>
                    {!notificacao.lida && (
                      <button 
                        className="btn btn-text"
                        onClick={() => handleMarcarNotificacaoLida(notificacao.id)}
                      >
                        Marcar como lida
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'perfil' && (
          <div className="perfil-section">
            <h2>Meu Perfil</h2>
            <form onSubmit={handleProfileUpdate} className="profile-form">
              <div className="form-group">
                <label htmlFor="nome">Nome</label>
                <input
                  type="text"
                  id="nome"
                  name="nome"
                  value={profileData.nome}
                  onChange={handleProfileChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={profileData.email}
                  onChange={handleProfileChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="telefone">Telefone</label>
                <input
                  type="tel"
                  id="telefone"
                  name="telefone"
                  value={profileData.telefone}
                  onChange={handleProfileChange}
                  pattern="[0-9]{10,15}"
                  title="Telefone deve ter entre 10 e 15 dígitos"
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Atualizar Perfil
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClienteDashboard;
